import{_ as t,o as r,c as a,a2 as o}from"./chunks/framework.BS-IksUQ.js";const p=JSON.parse('{"title":"About FOSSIL","description":"","frontmatter":{"title":"About FOSSIL"},"headers":[],"relativePath":"guide/index.md","filePath":"guide/index.md"}'),i={name:"guide/index.md"};function s(n,e,l,d,c,h){return r(),a("div",null,[...e[0]||(e[0]=[o('<h1 id="about-fossil" tabindex="-1">About FOSSIL <a class="header-anchor" href="#about-fossil" aria-label="Permalink to &quot;About FOSSIL&quot;">​</a></h1><p><strong>FOSSIL</strong> (FOrtran Stereo Litography parser) is a pure Fortran 2003+ library for reading, writing, and manipulating <a href="https://en.wikipedia.org/wiki/STL_(file_format)" target="_blank" rel="noreferrer">STL (stereolithography)</a> mesh files. It provides three OOP-designed types — <code>file_stl_object</code>, <code>surface_stl_object</code>, and <code>facet_object</code> — that together handle the full lifecycle from file I/O to advanced geometry operations.</p><p>Modern computational science and engineering tools often require STL mesh handling. FOSSIL fills that gap with a clean, user-friendly API — no C or C++ dependencies, no external mesh libraries.</p><p>The library is designed with a test-driven approach; every surface operation is exercised by an automated test program in <code>src/tests/</code>.</p><h2 id="authors" tabindex="-1">Authors <a class="header-anchor" href="#authors" aria-label="Permalink to &quot;Authors&quot;">​</a></h2><ul><li>Stefano Zaghi — <a href="https://github.com/szaghi" target="_blank" rel="noreferrer">@szaghi</a></li></ul><p>Contributions are welcome — see the <a href="./contributing.html">Contributing</a> page.</p><h2 id="copyrights" tabindex="-1">Copyrights <a class="header-anchor" href="#copyrights" aria-label="Permalink to &quot;Copyrights&quot;">​</a></h2><p>FOSSIL is distributed under a multi-licensing system:</p><table tabindex="0"><thead><tr><th>Use case</th><th>License</th></tr></thead><tbody><tr><td>FOSS projects</td><td><a href="http://www.gnu.org/licenses/gpl-3.0.html" target="_blank" rel="noreferrer">GPL v3</a></td></tr><tr><td>Closed source / commercial</td><td><a href="http://opensource.org/licenses/BSD-2-Clause" target="_blank" rel="noreferrer">BSD 2-Clause</a></td></tr><tr><td>Closed source / commercial</td><td><a href="http://opensource.org/licenses/BSD-3-Clause" target="_blank" rel="noreferrer">BSD 3-Clause</a></td></tr><tr><td>Closed source / commercial</td><td><a href="http://opensource.org/licenses/MIT" target="_blank" rel="noreferrer">MIT</a></td></tr></tbody></table><blockquote><p>Anyone interested in using, developing, or contributing to FOSSIL is welcome — pick the license that best fits your needs.</p></blockquote>',11)])])}const g=t(i,[["render",s]]);export{p as __pageData,g as default};
